sys_variable_value.value:
(function execute(inputs, outputs) {

    const sender = new PostalMailSender();
    const result = sender.createLetter(inputs.pdf, inputs.mail_configuration);
    outputs.letter_id = result.letter_id;
    outputs.letter = result.letter;

})(inputs, outputs);

